import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [name, setName] = useState('');
  const [year, setYear] = useState('');
  const [issue, setIssue] = useState('');
  const [publisher, setPublisher] = useState('');
  const [predictedPages, setPredictedPages] = useState('');
  const [correctedPages, setCorrectedPages] = useState('');

  const predictPages = () => {
    axios
      .post('http://0.0.0.0:8000/predict', {
        name: name,
        year: parseInt(year),
        issue: parseInt(issue),
        publisher: publisher,
      })
      .then((response) => {
        setPredictedPages(response.data['Predicted Pages']);
      })
      .catch((error) => {
        console.error('Prediction error:', error);
      });
  };

  const correctPages = () => {
    axios
      .post('http://0.0.0.0:8000/correct', {
        corrected_pages: parseFloat(correctedPages),
      })
      .then((response) => {
        alert(response.data.message);
      })
      .catch((error) => {
        console.error('Correction error:', error);
      });
  };

  return (
    <div>
      <h1>FastAPI and React Integration</h1>
      <div>
        <h2>Predict Pages</h2>
        <div>
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="number"
            placeholder="Year"
            value={year}
            onChange={(e) => setYear(e.target.value)}
          />
          <input
            type="number"
            placeholder="Issue"
            value={issue}
            onChange={(e) => setIssue(e.target.value)}
          />
          <input
            type="text"
            placeholder="Publisher"
            value={publisher}
            onChange={(e) => setPublisher(e.target.value)}
          />
          <button onClick={predictPages}>Predict</button>
        </div>
        <div>
          <p>Predicted Pages: {predictedPages}</p>
        </div>
      </div>
      <div>
        <h2>Correct Pages</h2>
        <div>
          <input
            type="number"
            placeholder="Corrected Pages"
            value={correctedPages}
            onChange={(e) => setCorrectedPages(e.target.value)}
          />
          <button onClick={correctPages}>Correct</button>
        </div>
      </div>
    </div>
  );
}

export default App;
